          const groupByUserCategory = groupBy("user");
          const resultGroupByUserCategory = groupByUserCategory(
            resultRanking[index]
          );

          const usuarioUnicos = resultRanking[index]
            .map((item) => item.user)
            .filter((value, index, self) => self.indexOf(value) === index);

          usuarioUnicos.forEach((item) => {
            let totalPrice = resultGroupByUserCategory[item].reduce(function(
              accumulator,
              item
            ) {
              return accumulator + item.points;
            },
            0);
            let userObject = {
              user: item,
              points: totalPrice,
            };
