            }
          }
        } else {
          console.log(`entro a la config  ----****----`);
          console.log(`game.votes.length: ${game.votes.length}`);
          if (game.votes.length > 0) {
            console.log(`configListSelected seteado`);
            console.log(
              `***********  parseInt(game.votes[0].key): ${parseInt(
                game.votes[0].key
              )}`
            );
            const categoriaElegida = parseInt(game.votes[0].key);
            if (!isNaN(categoriaElegida)) {
              if (categoriaElegida > 0 || categoriaElegida < 7) {
                game.configListSelected.push(game.votes[0].key);
                game.categorySelected = game.votes[0].key;
                game.totalLoopsConfig++;
              }
            }
          }
        }

        setTimeout(function() {
          stepFive();
        }, game.timeBtweenSteps);
      }
    };
    const stepFive = () => {
      console.log(`stepFive`);
      console.log(`game.isConfg: ${game.isConfig}`);
      console.log(`configList.length: ${configList.length}`);
      console.log(`game.totalLoopsConfig: ${game.totalLoopsConfig}`);
      if (game.isConfig === 0 && game.totalLoopsConfig === configList.length) {
        console.log(`config final`);
        console.log(`game.configListSelected: ${game.configListSelected}`);
        console.log(game.configListSelected);
        game.isConfig = 1;

        setPalabraParaAveriguar(game.configListSelected);
      }
      if (game.currentStep !== 999 && game.currentStep !== 666) {
        game.currentStep = 5;
        game.votes = [];
        console.log(`stepFive`);
        socket.emit("step", "5");
        setStatusStep(4);
        setTimeout(function() {
          stepOne();
        }, game.timeBtweenSteps);
      }
    };
    const setStatusStep = (current) => {
      if (current === 0) {
        game.steps[0].status = "current";
        game.steps[1].status = "upcoming";
        game.steps[2].status = "upcoming";
        game.steps[3].status = "upcoming";
        game.steps[4].status = "upcoming";
      }
      if (current === 1) {
        game.steps[0].status = "complete";
        game.steps[1].status = "current";
        game.steps[2].status = "upcoming";
      }
      if (current === 2) {
        game.steps[1].status = "complete";
        game.steps[2].status = "current";
        game.steps[3].status = "upcoming";
      }
      if (current === 3) {
        game.steps[2].status = "complete";
        game.steps[3].status = "current";
        game.steps[4].status = "upcoming";
      }
      if (current === 4) {
        game.steps[0].status = "complete";
        game.steps[1].status = "complete";
        game.steps[2].status = "complete";
        game.steps[3].status = "complete";
        game.steps[4].status = "current";
      }
    };
    const initGame = () => {
      clearInterval(game.timerW);
      clearInterval(game.timerL);
      console.log(`initGame`);
      console.log(configList);

      game.isConfig = 0;
      game.currentStep = 0;
      game.totalFallos = 0;
      game.delaySteps = 6000;
      game.timeBtweenStep = 1500;
      game.timeForVoting = 15000;
      game.seconds = 15;
      game.timeWinner = 10;
      game.votes = [];
      game.selectedMovie = [];
      game.totalLoopsConfig = 0;
      game.categorySelected = 0;
      game.configListSelected = [];

      game.rankingDisney = [];
      game.rankingAccion = [];
      game.rankingComedia = [];
      game.rankingAnimacion = [];
      game.rankingSeries = [];
      game.rankingAnime = [];

      socket.emit("getRanking");

      stepOne();
    };

    const setPalabraParaAveriguar = (categoriaSelected) => {
      console.log(`setPalabraParaAveriguar: ${categoriaSelected}`);
      let palabra = "";
      switch (parseInt(categoriaSelected)) {
        case 1:
          palabra =
            disneyMovies[Math.floor(Math.random() * disneyMovies.length)];
          break;
        case 2:
          palabra =
            accionMovies[Math.floor(Math.random() * accionMovies.length)];
          break;
        case 3:
          palabra =
            comediaMovies[Math.floor(Math.random() * comediaMovies.length)];
          break;
        case 4:
          palabra =
            animacionMovies[Math.floor(Math.random() * animacionMovies.length)];
          break;
        case 5:
          palabra = series[Math.floor(Math.random() * series.length)];
          break;
        case 6:
          palabra = animeSeries[Math.floor(Math.random() * animeSeries.length)];
          break;

        default:
          break;
      }
      game.stringMovie = palabra;
      console.log(`setPalabraParaAveriguar start. palabra: ${palabra}`);
      for (let index = 0; index < palabra.length; index++) {
        const element = palabra[index];
        const cleanElement = eliminarDiacriticos(String(element));
        const regex = /\W+/g;
        const myArray = regex.exec(cleanElement);
        let isSpecialCaracter =
          myArray !== null && myArray[0] !== " " ? true : false;
        let letraObject = {
          letra: cleanElement,
          original: element,
          selected: isSpecialCaracter ? isSpecialCaracter : false,
        };
        game.selectedMovie.push(letraObject);
      }
      console.log(`socket emit "initGame": ${palabra}`);
      socket.emit("initGame", palabra);
    };

    const cleanCadena = (cadena) => {
      return cadena
        .normalize("NFD")
        .replace(/[\u00C0-\u00FF]/g, "")
        .toLowerCase();
    };
    const eliminarDiacriticos = (s) => {
      if (s === "ñ") return "ñ";
      else return s.normalize("NFD").replace(/[\u0300-\u036f]/g, "");
    };

    // const eliminarDiacriticosEs = (s) => {
    //   return s.normalize(s, s.Form.NFD).replaceAll("[\u0300-\u0301]", "");
    // };
    return {
      ...toRefs(game),
      styleFallos,
      initGame,
      stepOne,
      stepTwo,
      stepThree,
      stepFour,
      stepFive,
      navLinks,
      configList,
      cleanCadena,
      eliminarDiacriticos,
      beforeEnter,
      enter,
      afterEnter,
      setPalabraParaAveriguar,
      categoriasJuego,
    };
  },
};
</script>
